import React, { useState, useRef } from 'react';
import axios from 'axios';
import ReactToPrint from 'react-to-print';
import CardComponent from './CardComponent';

function App() {
  const [start, setStart] = useState('');
  const [end, setEnd] = useState('');
  const [firstChar, setFirstChar] = useState('');
  const [startChar, setStartChar] = useState('');
  const [endChar, setEndChar] = useState('');
  const [numbers, setNumbers] = useState([]);
  const printRef = useRef();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/generate', {
        start: Number(start),
        end: Number(end),
      });
      setNumbers(response.data);
    } catch (error) {
      console.error('Error generating numbers:', error);
    }
  };

  return (
    <div className="App">
      <form onSubmit={handleSubmit}>
        <input
          type="number"
          value={start}
          onChange={(e) => setStart(e.target.value)}
          placeholder="Starting number"
          required
        />
        <input
          type="number"
          value={end}
          onChange={(e) => setEnd(e.target.value)}
          placeholder="Ending number"
          required
        />
        <input
          type="text"
          value={firstChar}
          onChange={(e) => setFirstChar(e.target.value)}
          placeholder="First character"
          maxLength={1}
          required
        />
        <input
          type="text"
          value={startChar}
          onChange={(e) => setStartChar(e.target.value)}
          placeholder="Starting character"
          maxLength={1}
          required
        />
        <input
          type="text"
          value={endChar}
          onChange={(e) => setEndChar(e.target.value)}
          placeholder="Ending character"
          maxLength={1}
          required
        />
        <button type="submit">Generate Cards</button>
      </form>
      <CardComponent
        firstChar={firstChar}
        startChar={startChar}
        endChar={endChar}
        startNum={Number(start)}
        endNum={Number(end)}
      />
      <ReactToPrint
        trigger={() => <button>Print Cards</button>}
        content={() => printRef.current}
      />
      <div style={{ display: 'none' }}>
        <CardComponent
          ref={printRef}
          firstChar={firstChar}
          startChar={startChar}
          endChar={endChar}
          startNum={Number(start)}
          endNum={Number(end)}
        />
      </div>
    </div>
  );
}

export default App;
